import React, { useState } from 'react';
import firebase from "../firebase"
import { Text, TextInput, View, Pressable } from 'react-native';
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import tw from "twrnc";

const Login = () => {
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');

  const auth = getAuth(); 

  signInWithEmailAndPassword(auth, "alumno2@gmail.com", "alumno123")
  .then((userCredential) => {
    // Signed in
    const user = userCredential.user;
    console.log("Signed in");
    // ...
  })
  .catch((error) => {
    console.log("Not signed in");
    const errorCode = error.code;
    const errorMessage = error.message;
  });



  return (
    <View style={tw`bg-slate-900 h-full w-full items-center `}> 
      <View style={tw`w-7/12 h-3/6 border-2 border-gray-700 bg-gray-200 mt-30 py-2 items-center rounded-lg`}>
        <TextInput
          style={tw`bg-gray-300 w-10/12 h-12 my-4 border-2 border-gray-700`}
          placeholder="Ingrese su correo"
          onChangeText={newText => setUser(newText)}
          defaultValue={user}
        />
        <TextInput
          style={tw`bg-gray-300 w-10/12 h-12 my-4 border-2 border-gray-700`}
          placeholder="Ingrese su contraseÃ±a"
          onChangeText={newText => setPassword(newText)}
          defaultValue={password}
        />
        <Pressable style={tw`w-20 h-10 bg-slate-800 items-center rounded-lg`} onPress={signInWithEmailAndPassword}>
          <Text style={tw`text-white mt-3`}> Login </Text>
        </Pressable>
      </View>

    </View>
  );
}



export default Login;
