import { View, Text } from "react-native";
import { StatusBar } from "expo-status-bar";
import React, {useEffect, useState} from "react";
import tw from "twrnc";
import { collection, query, getDocs, db } from "firebase/firestore";


export default function Loading() {
  const [alarmas, setAlarmas] = useState([]);

  useEffect(() => {
    const q = query(collection("Alertas"));
    const querySnapshot = getDocs(q);
    let listaAlarmas = []
    querySnapshot.forEach((doc) => {
      listaAlarmas.push({ ...doc.data(), id: doc.id })
    });
    setAlarmas(listaAlarmas)
    //console.log(alarmas);
  }, [])

  return (
    <View style={tw`bg-slate-900 justify-center items-center h-full`}>
      <Text style={tw`text-white text-xl font-bold`}></Text>
      <StatusBar style="auto" />
      <View>
        {alarmas.map((alarma) => {
          console.log(alarma.Zona)
        })}
      </View>
    </View>
  );
}
